mapper.update:
UPDATE workflow
SET name = #{name}, description = #{description}, version = #{version},
    status = #{status}, updated_at = #{updatedAt}, definition = #{definition}
WHERE id = #{id}

UPDATE workflow_instance
SET status = #{status}, output_params = #{outputParams}, finished_at = #{finishedAt}, updated_at = #{updatedAt}
WHERE id = #{id}
